import React, { Component } from 'react';
import './index.css';
import CheckoutTable from '../../components/checkoutTable';
import { Elements } from 'react-stripe-elements';
import CheckoutForm from '../../components/checkoutForm';


class Checkout extends Component {
  constructor() {
    super();

    this.state = {
      'checkout': []
    }
  }

  saveCheckout = async(e) => {
    e.preventDefault();
    let title = e.target.elements.title.value;
    let price = e.target.elements.price.value;
    let quantity = e.target.elements.quantity.value;
    let name = e.target.elements.name.value;
    let email = e.target.elements.email.value;
    let street = e.target.elements.street.value;
    let city = e.target.elements.city.value;
    let state = e.target.elements.state.value;
    let country = e.target.elements.country.value;

    let URL = 'http://localhost:5000/api/save/checkout';

    let response = await fetch(URL, {
      "method": "POST",
      "headers": {
        "Content-Type": "application/json",
        "title": title,
        "price": price,
        "quantity": quantity,
        "name": name,
        "email": email,
        "street": street,
        "city": city,
        "state": state,
        "country": country
      }
    });

    let data = await response.json();
    console.log(data);

    if (data.success) {
      alert('Checkout Saved! We will ship your products ASAP!')
    } else {
      alert('Checkout was not recieved. Pleast try again!')
    }
  }



  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-10 offset-md-1">
            <h1>Checkout</h1>
            <CheckoutTable
              cart={this.props.cart}
              removeItem={this.props.removeItem}
              total={this.props.total}
            />
            <Elements>
              <CheckoutForm
              total={this.props.total}
              />
            </Elements>
          </div> {/*ends col */}
        </div> {/*ends row */}
      </div>
    );
  }
}

export default Checkout;
