import React from 'react';
import { CardElement, injectStripe } from 'react-stripe-elements';


class CheckoutForm extends React.Component {
  constructor() {
    super();

    this.state = {
      completed: false,
      name: '',
      street: '',
      city: '',
      state: '',
      zipcode: '',
    }
  }

  convertToPennies = () => {
    let total = this.props.total;

    total *= 100;

    total = Math.floor(total);

    return total;
  }

  handleSubmit = async(e) => {
    let { token } = await this.props.stripe.createToken({name:"Name"});

    let total = this.convertToPennies();
    let email = this.state.email
    let name = this.state.name
    let city = this.state.city
    let street = this.state.street
    let state = this.state.state
    let zipcode = this.state.zipcode


    let URL = 'http://localhost:5000/api/payment';

    let response = await fetch(URL, {
      method: "POST",
      headers: {
        "Content-Type" : "application/json",
        "token" : token.id,
        "email" : email,
        "amount" : total,
        "name": name,
        "street": street,
        "city": city,
        "state": state,
        "zipcode": zipcode,
      }
    });

    let data = await response.json();

    if (data.message === 'success') {
      this.setState({ completed : true });
    }
  }

  render() {
    if (this.state.completed) return <h2>Purchase Complete</h2>
    return (
      <div className="checkout">
        <p>Would you like to complete the purchase?</p>
        <CardElement />
        <div className="form-group">
          <label><b>Email:</b></label>
          <input value={this.state.email} onChange={(e) => this.setState({ 'email' : e.target.value })} type="text" className="form-control" name="email" />
        </div>
        <div className="form-group">
          <label><b>Name:</b></label>
          <input value={this.state.name} onChange={(e) => this.setState({ 'name' : e.target.value })} type="text" className="form-control" name="name" />
        </div>
        <div className="form-group">
          <label><b>Street Address:</b></label>
          <input value={this.state.street} onChange={(e) => this.setState({ 'street' : e.target.value })} type="text" className="form-control" name="street" />
        </div>
        <div className="form-group">
          <label><b>City:</b></label>
          <input value={this.state.city} onChange={(e) => this.setState({ 'city' : e.target.value })} type="text" className="form-control" name="city" />
        </div>
        <div className="form-group">
          <label><b>State:</b></label>
          <input value={this.state.state} onChange={(e) => this.setState({ 'state' : e.target.value })} type="text" className="form-control" name="state" />
        </div>
        <div className="form-group">
          <label><b>Zip Code:</b></label>
          <input value={this.state.zipcode} onChange={(e) => this.setState({ 'zipcode' : e.target.value })} type="text" className="form-control" name="zipcode" />
        </div>
        <button className="purchase" onClick={() => this.handleSubmit()}>Purchase</button>
      </div>
    )
  }
}

export default injectStripe(CheckoutForm);
